import React from 'react'
import {connect} from 'react-redux'
import { NavBar } from 'antd-mobile';
function Boss() {
  return <h2>Boss首页</h2>
}
function Genius() {
  return <h2>牛人首页</h2>
}
function Msg() {
  return <h2>消息列表首页</h2>
}
function User() {
  return <h2>个人中心页面</h2>
}
class Dashboard extends React.Component{
  render() {
    const {pathname} = this.props.location
    const user = this.props.user
    const navList = [
      {
        path: '/boss',
        text: '牛人',
        icon: 'boss',
        title: '牛人列表',
        component: Boss,
        hide: user.type==='genius'
      },
      {
        path: '/genius',
        text: 'boss',
        icon: 'job',
        title: 'Boss列表',
        component: Genius,
        hide: user.type==='boss'
      },
      {
        path: '/msg',
        text: '消息',
        icon: 'msg',
        title: '消息列表',
        component: Msg
      },
      {
        path: '/me',
        text: '我',
        icon: 'user',
        title: '个人中心',
        component: User
      }
    ]
    return (
      <div>
        <NavBar mode='dard'>{navList.find(v=>v.path===pathname).title}</NavBar>
        <h2>footer</h2>
      </div>
    )
  }
}

export default connect(state => state)(Dashboard)
